import { Component } from '@angular/core';
import { CalculationService } from "../../services/calc.service";
import { Router } from "@angular/router";
import { LanguageService } from '../../services/language.service';
@Component({
  selector: 'app-history',
  templateUrl: './history.component.html',
  styleUrls: ['./history.component.css']
})
export class HistoryComponent {
  selectedCalculations: string = "BA";
  inputValue: string = '';
  isInputFocused: boolean = false;
  calculationResult: string | undefined;
  calculationTime: number | undefined;
  
inputError: string = '';
  constructor(
    private calcService: CalculationService,
    private router: Router,
    private languageService: LanguageService
  ) { }
 ngOnInit() {
    this.languageService.languageChanged.subscribe(() => {
      this.languageChangedCallback();
    });
    const userId: string = localStorage.getItem('userId') as string;
    var userIdNum = parseInt(userId);
    console.log(userId)
    var myHeaders =  new Headers()
    myHeaders.append('Authorization', 'Bearer ' + localStorage.getItem('token'));
    this.calcService.createCalculationHistory(userIdNum, myHeaders).subscribe(
      response => {
        console.log(response);
        let list = document.getElementById("list")!;
        for (let i = response.data.length - 1; i >= 0; i--) {
            let li = document.createElement('li');
            li.setAttribute('style', 'color: #666666; \
            font-size: 16px;n \
            margin-bottom: 10px; \
            padding: 10px; \
            border: 1px solid #cccccc; \
            border-radius: 5px; \
            list-style-type: none; \
            color: rgba(0, 0, 0, 0.8);');
            li.innerText = response.data[i].date + " " + response.data[i].library  + " " + response.data[i].result
            + " " + response.data[i].inputData + " " + response.data[i].time + " " + response.data[i].threadsUsed
            + " " + response.data[i].type;
            list.appendChild(li);
        }
      },
      error => {
        console.log(error.error.error);        
      }
    )
  }

  languageChangedCallback() {
    this.inputError = '';
  }

  isLoggedIn(): boolean {
    return !!localStorage.getItem('email');
  }
}
