import { Component } from '@angular/core';
import { CalculationService } from "../../services/calc.service";
import {  MatrixSum, Matrix } from "../../interfaces/calculation";
import { Router } from "@angular/router";
import { LanguageService } from '../../services/language.service';

@Component({
  selector: 'app-matrix-component',
  templateUrl: './matrix-component.component.html',
  styleUrls: ['./matrix-component.component.css']
})
export class MatrixComponentComponent {
  inputValue: string = '';
  isInputFocused: boolean = false;
  calculationResult: string | undefined;

inputError: string = '';
  constructor(
    private calcService: CalculationService,
    private router: Router,
    private languageService: LanguageService
  ) { }
 ngOnInit() {
    this.languageService.languageChanged.subscribe(() => {
      this.languageChangedCallback();
    });
  }

  languageChangedCallback() {
    this.inputError = '';
  }
  onInputFocus() {
    this.isInputFocused = true;
  }

  onInputBlur() {
    setTimeout(() => {
      this.isInputFocused = false;
    }, 200);
  }

  addCharacter(character: string) {
    this.inputValue += character;
  }

  deleteLastCharacter() {
    this.inputValue = this.inputValue.slice(0, -1);
  }

  clearInput() {
    this.inputValue = '';
  }


getTranslation(key: string): string {
  return this.languageService.getTranslation(key);
}
MatrixSum(){
  const userId = localStorage.getItem('userId');

  const matrix1: Matrix = {
      rows: 2,
      cols: 2,
      data: [[1,2], [3,4]]
  };
  const matrix2: Matrix = {
    rows: 2,
    cols: 2,
    data: [[-1,-2], [-3,-5]]
};
    const calcData: MatrixSum = {
      userId: userId,
      matrix1: matrix1,
      matrix2: matrix2,
      threadsUsed: 1,
      library: localStorage.getItem("Library") as string
    };

    this.calcService.createCalculationMatrixSum(calcData as MatrixSum).subscribe(
      response => {
        let result: string = "";
        for(let i = 0; i < response.data.size[0]; i++){
          result += "[";
          for(let j = 0; j < response.data.size[1]; j++){
            result += response.data.data[i][j] + ", "
            console.log("1");
          }
          result = result.slice(0,result.length - 2)
          result += "], ";
        }
        this.calculationResult = result;
      },
      error => {
        this.calculationResult = error.error.error;
      }
    );
  }
}
