  }

  matrixSystem(){
    const userId = localStorage.getItem('userId');
    let M1: number[][] = [];
    for (let i = 0 ; i < this.Arows; i ++){
      M1[i] = [];
      for (let j = 0 ; j < this.Acols; j ++)
          M1[i][j] =  this.Matrix1[i][j];
    }
    const matrix1: Matrix = {
        rows: this.Arows,
        cols: this.Acols,
        data: M1
    };
    let M2: number[][] = [];
  for (let i = 0 ; i < this.Brows; i ++){
    M2[i] = [];
    for (let j = 0 ; j < this.Bcols; j ++)
        M2[i][j] =  this.Matrix2[i][j];
  }
    const matrix2: Matrix = {
      rows: this.Brows,
        cols: this.Bcols,
      data: M2
  };
      const calcData: MatrixSystem = {
        userId: userId,
        matrix1: matrix1,
        matrix2: matrix2,
        threadsUsed: this.ThreadCount,
        library: localStorage.getItem("Library") as string
      };

      this.calcService.createCalculationMatrixSystem(calcData as MatrixSystem).subscribe(
        response => {
          const obj : Result = {
            time : response.data.time,
            result : response.data.result
          }
          this.resultEventM.emit(obj);
        },
        error => {
          this.resultEventM.emit(error.error.error);
        }
      );
  }

  clear(matrix: number[][]){
    for(let i = 0; i < matrix.length; i++)
      for(let j = 0; j < matrix[i].length; j++)
        matrix[i][j]=0;
  }

  transpose(matrix: number[][], rows: number , cols: number){

    const userId = localStorage.getItem('userId');
    let M: number[][] = [];
  for (let i = 0 ; i < rows; i ++){
    M[i] = [];
    for (let j = 0 ; j < cols; j ++)
        M[i][j] =  matrix[i][j];
  }
    const matrix1: Matrix = {
        rows: rows,
        cols: cols,
        data: M
    };
      const calcData: MatrixTranspose = {
        userId: userId,
        matrix: matrix1,
        library: localStorage.getItem("Library") as string
      };

      this.calcService.createCalculationMatrixTranspose(calcData as MatrixTranspose).subscribe(
        response => {
          let result: string = "";
          for(let i = 0; i < response.data.result.size[0]; i++){
            result += "[";
            for(let j = 0; j < response.data.result.size[1]; j++){
              result += response.data.result.data[i][j] + ", "
            }
            result = result.slice(0,result.length - 2)
