import { Component } from '@angular/core';
import { CalculationService } from "../../services/calc.service";
import { BasicArithmetic, Equation, MatrixSum, Matrix } from "../../interfaces/calculation";
import { Router } from "@angular/router";
import { LanguageService } from '../../services/language.service';

@Component({
  selector: 'app-input',
  templateUrl: './input-component.component.html',
  styleUrls: ['./input-component.component.css']
})
export class InputComponent {
  selectedCalculations: string = "BA";
  inputValue: string = '';
  isInputFocused: boolean = false;
  calculationResult: string | undefined;
  
inputError: string = '';
  constructor(
    private calcService: CalculationService,
    private router: Router,
    private languageService: LanguageService
  ) { }
 ngOnInit() {
    this.languageService.languageChanged.subscribe(() => {
      this.languageChangedCallback();
    });
  }

  languageChangedCallback() {
    this.inputError = '';
  }
  onInputFocus() {
    this.isInputFocused = true;
  }

  onInputBlur() {
    setTimeout(() => {
      this.isInputFocused = false;
    }, 200);
  }

  addCharacter(character: string) {
    this.inputValue += character;
  }

  deleteLastCharacter() {
    this.inputValue = this.inputValue.slice(0, -1);
  }

  clearInput() {
    this.inputValue = '';
  }

  calculateBasicArithmetic() {
    const userId = localStorage.getItem('userId');

    const calcData: BasicArithmetic = {
      userId: userId,
      expression: this.inputValue,
      library: localStorage.getItem("Library") as string
    };

    this.calcService.createCalculationBasicArithmetic(calcData as BasicArithmetic).subscribe(
      response => {
        this.calculationResult = response.data;
      },
      error => {
        this.calculationResult = error.error.error;
      }
    );
  }

  calculateEquation() {
    const userId = localStorage.getItem('userId');

    const calcData: Equation = {
      userId: userId,
      equation: this.inputValue,
      library: localStorage.getItem("Library") as string
    };

    this.calcService.createCalculationEquation(calcData as Equation).subscribe(
      response => {

        this.calculationResult = response.data;
      },
      error => {
        this.calculationResult = error.error.error;
      }
    );
  }

getTranslation(key: string): string {
    return this.languageService.getTranslation(key);
  }

changeCalc(key: string){
  this.selectedCalculations = key;
  if (this.selectedCalculations == "M")
    this.MatrixCalculationsDOM();
  else if (this.selectedCalculations == "BA")
    this.BasicArithmeticCalculationsDOM();
  else if (this.selectedCalculations == "Eq")
    this.EquationCalculationsDOM();
}

MatrixSum(){
  const userId = localStorage.getItem('userId');

  const matrix1: Matrix = {
      rows: 2,
      cols: 2,
      data: [[1,2], [3,4]]
  };
  const matrix2: Matrix = {
    rows: 2,
    cols: 2,
    data: [[-1,-2], [-3,-5]]
};
    const calcData: MatrixSum = {
      userId: userId,
      matrix1: matrix1,
      matrix2: matrix2,
      threadsUsed: 1,
      library: localStorage.getItem("Library") as string
    };

    this.calcService.createCalculationMatrixSum(calcData as MatrixSum).subscribe(
      response => {
        let result: string = "";
        for(let i = 0; i < response.data.size[0]; i++){
          result += "[";
          for(let j = 0; j < response.data.size[1]; j++){
            result += response.data.data[i][j] + ", "
            console.log("1");
          }
          result = result.slice(0,result.length - 2)
          result += "], ";
        }
        this.calculationResult = result;
      },
      error => {
        this.calculationResult = error.error.error;
      }
    );
  }

  async MatrixCalculationsDOM(){
      const mDiv = document.getElementsByName("calculationSpace")[0];
      console.log(mDiv);
      
  }
  BasicArithmeticCalculationsDOM(){
    const mDiv = document.getElementsByName("calculationSpace")[0];
    const sp = mDiv.getElementsByTagName("span")[0];
    if (sp != undefined)
      mDiv.removeChild(sp);
    const el = document.createElement("span")

    el.insertAdjacentHTML("afterbegin",'<div class="input-container"> <input type="text" placeholder="{{ getTranslation('+"'"+
      'Enter what you want to calculate or know about' +"'"+ ') }}" [(ngModel)]="inputValue" (focus)="onInputFocus()" (blur)="onInputBlur()">' +
      '<button class="btn orange-bg" (click)="calculateBasicArithmetic()">{{ getTranslation('+"'"+'='+"'"+') }}</button>' +
    '</div>  <div class="math-input-container" [hidden]="!isInputFocused">' +
      '<table>'+
        '<tbody>'+
          '<tr>'+
            '<td><button class="btn" (click)="addCharacter('+"'1'"+')">1</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"''2'"+')">2</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"'3'"+')">3</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"'('"+')">(</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"')'"+')">)</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"'^'"+')">^</button></td>'+
          '</tr>'+
          '<tr>'+
            '<td><button class="btn" (click)="addCharacter('+"'4'"+')">4</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"'5'"+')">5</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"'6'"+')">6</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"'+'"+')">+</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"'-'"+')">-</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"'π'"+')">π</button></td>'+
          '</tr>'+
          '<tr>'+
            '<td><button class="btn" (click)="addCharacter('+"'8'"+')">8</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"'9'"+')">9</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"'*'"+')">*</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"'/'"+')">/</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"'e'"+')">e</button></td>'+
          '</tr>'+
          '<tr>'+
            '<td><button class="btn" (click)="addCharacter('+"'0'"+')">0</button></td>'+
            '<td><button class="btn" (click)="addCharacter('+"'.'"+')">.</button></td>'+
            '<td><button class="btn" (click)="clearInput()">C</button></td>'+
            '<td colspan="2"><button class="btn" (click)="deleteLastCharacter()">{{ getTranslation('+"'Backspace'"+') }}</button></td>'+
          '</tr></tbody></table></div>');
      mDiv.appendChild(el);
        
  }

  EquationCalculationsDOM(){

  }
}

<table width="100%">
  <tbody>
    <tr>
      <td width="58%">

        <div [ngSwitch]="selectedCalculations">
          <span *ngSwitchCase="'BA'">
            <app-basic-arithemtic-component></app-basic-arithemtic-component>
            </span>
            <span *ngSwitchCase="'Eq'">
              <div class="input-container">
                <input type="text" placeholder="{{ getTranslation('Enter what you want to calculate or know about') }}" [(ngModel)]="inputValue" (focus)="onInputFocus()" (blur)="onInputBlur()">
                <button class="btn orange-bg" (click)="calculateEquation()">{{ getTranslation('=') }}</button>
              </div>            
              <div class="math-input-container" [hidden]="!isInputFocused">
                <table>
                  <tbody>
                    <tr>
                      <td><input type="text" [(ngModel)]="inputValue" (focus)="onInputFocus()" (blur)="onInputBlur()"></td>
                      <td><button class="btn" (click)="addCharacter('2')">2</button></td>
                      <td><button class="btn" (click)="addCharacter('3')">3</button></td>
                      <td><button class="btn" (click)="addCharacter('(')">(</button></td>
                      <td><button class="btn" (click)="addCharacter(')')">)</button></td>
                      <td><button class="btn" (click)="addCharacter('^')">^</button></td>
                    </tr>
                    <tr>
                      <td><button class="btn" (click)="addCharacter('4')">4</button></td>
                      <td><button class="btn" (click)="addCharacter('5')">5</button></td>
                      <td><button class="btn" (click)="addCharacter('6')">6</button></td>
                      <td><button class="btn" (click)="addCharacter('+')">+</button></td>
                      <td><button class="btn" (click)="addCharacter('-')">-</button></td>
                      <td><button class="btn" (click)="addCharacter('π')">π</button></td>
                    </tr>
                    <tr>
                      <td><button class="btn" (click)="addCharacter('7')">7</button></td>
                      <td><button class="btn" (click)="addCharacter('8')">8</button></td>
                      <td><button class="btn" (click)="addCharacter('9')">9</button></td>
                      <td><button class="btn" (click)="addCharacter('*')">*</button></td>
                      <td><button class="btn" (click)="addCharacter('/')">/</button></td>
                      <td><button class="btn" (click)="addCharacter('e')">e</button></td>
                    </tr>
                    <tr>
                      <td><button class="btn" (click)="addCharacter('0')">0</button></td>
                      <td><button class="btn" (click)="addCharacter('.')">.</button></td>
                      <td><button class="btn" (click)="clearInput()">{{ getTranslation('C') }}</button></td>
                      <td><button class="btn" (click)="addCharacter('x')">{{ getTranslation('x') }}</button></td>
                      <td colspan="2"><button class="btn" (click)="deleteLastCharacter()">{{ getTranslation('Backspace') }}</button></td>
                    </tr>
                  </tbody>
                </table>
              </div></span>
              <span name="matrixDiv" *ngSwitchCase="'M'">           
                <div name="matrixDiv" class="math-input-container" [hidden]="!isInputFocused">
                  
                </div>  
              </span>
        </div>
        
      </td>
      <td width="42%">
          <ul>
            <li style="display:inline"><button class="btn-choice" (click)="changeCalc('BA')">{{ getTranslation('Basic Arithmetics') }}</button></li>
            <li style="display:inline"><button class="btn-choice" (click)="changeCalc('Eq')">{{ getTranslation('Equations') }}</button></li>
            <li style="display:inline"><button class="btn-choice" (click)="changeCalc('M')">{{ getTranslation('Matrix') }}</button></li>
          </ul>
      </td>
    </tr>
  </tbody>
</table>


<div name="result" class="result-container" *ngIf="calculationResult !== ''">
  <p>{{ getTranslation('Result') }}:</p>
  <p>{{ calculationResult }}</p>
</div>
