import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { LanguageService } from '../../services/language.service';
import { Subscription } from 'rxjs';
import { EventEmitter } from '@angular/core';
import {AuthService} from "../../services/auth.service";
import { MessageService } from 'primeng/api';
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  selectedLanguage: string;
  
  selectedLibrary: string = "";
  showLanguageMenu: boolean = false;
  showlibraryMenu: boolean = false;
  showhistory: boolean = false;
  languageChanged: EventEmitter<string> = new EventEmitter<string>();
  private languageSubscription: Subscription | undefined;

  constructor(private router: Router, private languageService: LanguageService, private authService: AuthService, private msgService: MessageService) {
    this.selectedLanguage = this.languageService.getLanguage();
    this.authService = authService;
  }

  ngOnInit() {
    this.languageSubscription = this.languageService.selectedLanguageChanged.subscribe(() => {
      this.selectedLanguage = this.languageService.getLanguage();
    });
    this.selectedLibrary = "JAVA";
    localStorage.setItem("Library", "JAVA");
  }

  changeLanguage(language: string): void {
    this.languageService.setLanguage(language);
    this.languageService.selectedLanguageChanged.next(language);
    this.showLanguageMenu = !this.showLanguageMenu;
  }

  ngOnDestroy() {
    this.languageSubscription?.unsubscribe();
  }

  logOut() {
    localStorage.clear();
    // this.authService.logout().subscribe(
    //   response=>{
    //     console.log(response);
    //   }
    // );
    this.router.navigate(['/login']);
  }

  toggleLanguageMenu(): void {
    this.showLanguageMenu = !this.showLanguageMenu;
  }

  isLoggedIn(): boolean {
    return !!localStorage.getItem('email');
  }

  getTranslation(key: string): string {
    return this.languageService.getTranslation(key);
  }

  goToChat(): void {
    this.router.navigate(['/chat']);
  }

  toggleLibraryMenu(): void {
      this.showlibraryMenu = !this.showlibraryMenu;
  }

  changeLibrary(library: string): void {
    localStorage.setItem("Library", library);
    this.selectedLibrary = library;
    this.showlibraryMenu = !this.showlibraryMenu;
  }

  toggleHistory(): void {
    if (this.isLoggedIn())
      this.showhistory = !this.showhistory;
    else{
      this.msgService.add({ severity: 'error', summary: 'Error', detail: 'You must be an authorized user' });
    }
  }
}
<div className="landing-container">

  <header className="landing-header">
    <div className="logo">SpeedWagon</div>
    <nav className="nav-bar">
      <button>Найти</button>
      <button>Маршруты</button>
      <button>Запланированные поездки</button>
      <button className="profile-button">
        <img
          src="/path/to/profile-icon.png"
          alt="Профиль"
          className="profile-icon"
        />
      </button>
    </nav>
  </header>

  <main className="landing-main">
    <div className="search-section">
      <input type="text" placeholder="Откуда?" />
      <input type="text" placeholder="Куда?" />
      <input type="date" />
      <select>
        <option>1 Пассажир</option>
        <option>2 Пассажира</option>
        <option>3 Пассажира</option>
      </select>
      <button className="search-button">Найти</button>
    </div>

    <div className="popular-routes">
      <h2>Популярные Маршруты</h2>
      <!-- <div className="routes-grid">
        {[
          "Минск - Могилев от 20 руб.",
          "Минск - Гомель от 20 руб.",
          "Минск - Брест от 20 руб.",
          "Минск - Витебск от 20 руб.",
          "Минск - Гродно от 15 руб.",
          "Минск - Бельницы от 20 руб.",
        ].map((route, index) => (
          <button key={index} className="route-button">
            {route}
          </button>
        ))}
      </div> -->
    </div>

    <div className="app-info">
      <h2>Доступно Мобильное Приложение</h2>
      <p>Все ваши билеты прямо в телефоне!</p>
      <div className="app-details">
        <img
          src="/path/to/app-mockup.png"
          alt="Мобильное приложение"
          className="app-image"
        />
        <div className="app-text">
          <h3>Экономия</h3>
          <p>
            Наши технологии позволяют увеличивать эффективность работы наших
            партнеров. Поэтому мы можем делать цены ниже.
          </p>
          <h3>Широкий выбор маршрутов</h3>
          <p>
            От коротких маршрутов до дальних путешествий. Независимо от места
            назначения — мы поможем найти идеального перевозчика.
          </p>
          <h3>Поддержка 24/7</h3>
          <p>
            Напишите нам в чат или позвоните по телефону, наши операторы
            ответят вам на все вопросы.
          </p>
        </div>
      </div>
    </div>
  </main>

  <footer className="landing-footer">
    <div className="footer-links">
      <div>
        <h3>SpeedWagon</h3>
        <ul>
          <li>О нас</li>
          <li>Помощь</li>
          <li>Политика конфиденциальности</li>
        </ul>
      </div>
      <div>
        <h3>Сотрудничество</h3>
        <ul>
          <li>Стать партнером</li>
        </ul>
      </div>
      <div>
        <h3>Социальные сети</h3>
        <ul className="social-icons">
          <li>VK</li>
          <li>Telegram</li>
          <li>Instagram</li>
        </ul>
      </div>
    </div>
    <p>2024 © SpeedWagon. Все права защищены.</p>
  </footer>
</div>
