import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { MessageService } from 'primeng/api';
import { User } from 'src/app/interfaces/auth';
import { AuthService } from 'src/app/services/auth.service';
import { passwordMatchValidator } from 'src/app/shared/password-match.directive';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { LanguageService } from '../../services/language.service';
@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css'],
})
export class RegisterComponent {
inputError: string = '';
  registerForm = this.fb.group({
    firstname: ['', [Validators.required, Validators.pattern(/^[a-zA-Z]+(?: [a-zA-Z]+)*$/)]],
    lastname: ['', [Validators.required, Validators.pattern(/^[a-zA-Z]+(?: [a-zA-Z]+)*$/)]],
    surname: ['', [Validators.required, Validators.pattern(/^[a-zA-Z]+(?: [a-zA-Z]+)*$/)]],
    phone: ['', [Validators.required, Validators.pattern(/^\+375(25|29|33|44)\d{7}$/)]],
    email: ['', [Validators.required, Validators.email]],
    password: ['', Validators.required],
    confirmPassword: ['', Validators.required]
  }, {
    validators: passwordMatchValidator
  });

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private messageService: MessageService,
    private router: Router,
private languageService: LanguageService
  ) {}
languageChangedCallback() {
  this.inputError = this.languageService.getTranslation('Input Error');
  console.log('Current language:', this.languageService.getLanguage());
  console.log('Translated input error:', this.inputError);
}
getTranslation(key: string): string {
    return this.languageService.getTranslation(key);
  }
  get firstname() {
    return this.registerForm.controls.firstname;
  }
  get lastname() {
    return this.registerForm.controls.lastname;
  }
  get surname() {
    return this.registerForm.controls.surname;
  }
  get phone() {
    return this.registerForm.controls.phone;
  }

  get email() {
    return this.registerForm.controls.email;
  }

  get password() {
    return this.registerForm.controls.password;
  }

  get confirmPassword() {
    return this.registerForm.controls.confirmPassword;
  }

  submitDetails() {
    const postData = { ...this.registerForm.value };
    delete postData.confirmPassword;

    this.authService.registerUser(postData as User).subscribe(
      response => {
        console.log(response);
        this.messageService.add({ severity: 'success', summary: 'Success', detail: 'Registered successfully' });
        this.router.navigate(['login']);
      },
      error => {
        this.messageService.add({ severity: 'error', summary: 'Error', detail: error.error.error });
      }
    );
  }
}
<form [formGroup]="registerForm" (ngSubmit)="submitDetails()" class="orange-form">
  <p-card header="{{ getTranslation('Create an account') }}" [style]="{ width: '360px' }">
    <div class="field">
      <label for="firstname">{{ getTranslation('FirstName') }}</label>
      <input type="text" pInputText formControlName="firstname" class="w-full" name="firstname" placeholder="{{ getTranslation('Type your name') }}" />
      <small *ngIf="firstname.invalid && (firstname.dirty || firstname.touched)" class="block p-error">
        <div *ngIf="firstname.errors?.['required']">{{ getTranslation('Name is required.') }}</div>
        <div *ngIf="firstname.errors?.['pattern']">{{ getTranslation('Enter a valid name.') }}</div>
      </small>
    </div>
    <div class="field">
      <label for="lastname">{{ getTranslation('Lastname') }}</label>
      <input type="text" pInputText formControlName="lastname" class="w-full" name="lastname" placeholder="{{ getTranslation('Type your name') }}" />
      <small *ngIf="lastname.invalid && (lastname.dirty || lastname.touched)" class="block p-error">
        <div *ngIf="lastname.errors?.['required']">{{ getTranslation('Name is required.') }}</div>
        <div *ngIf="lastname.errors?.['pattern']">{{ getTranslation('Enter a valid name.') }}</div>
      </small>
    </div>
    <div class="field">
      <label for="surname">{{ getTranslation('Surname') }}</label>
      <input type="text" pInputText formControlName="surname" class="w-full" name="surname" placeholder="{{ getTranslation('Type your name') }}" />
      <small *ngIf="surname.invalid && (surname.dirty || surname.touched)" class="block p-error">
        <div *ngIf="surname.errors?.['required']">{{ getTranslation('Name is required.') }}</div>
        <div *ngIf="surname.errors?.['pattern']">{{ getTranslation('Enter a valid name.') }}</div>
      </small>
    </div>
    <div class="field">
      <label for="email">{{ getTranslation('Email') }}</label>
      <input type="text" pInputText formControlName="email" class="w-full" name="email" placeholder="{{ getTranslation('Type your email') }}" />
      <small *ngIf="email.invalid && (email.dirty || email.touched)" class="block p-error">
        <div *ngIf="email.errors?.['required']">{{ getTranslation('Email is required.') }}</div>
        <div *ngIf="email.errors?.['email']">{{ getTranslation('Email should be valid.') }}</div>
      </small>
    </div>
    <div class="field">
      <label for="password">{{ getTranslation('Password') }}</label>
      <input type="password" pInputText formControlName="password" class="w-full" name="password" placeholder="{{ getTranslation('Type your password') }}" />
      <small *ngIf="password.invalid && (password.dirty || password.touched)" class="block p-error">
        <div *ngIf="password.errors?.['required']">{{ getTranslation('Password is required.') }}</div>
      </small>
    </div>
    <div class="field">
      <label for="confirmPassword">{{ getTranslation('Confirm Password') }}</label>
      <input type="password" pInputText formControlName="confirmPassword" class="w-full" name="confirmPassword" placeholder="{{ getTranslation('Confirm Password') }}" />
      <small *ngIf="confirmPassword.invalid && (confirmPassword.dirty || confirmPassword.touched)" class="block p-error">
        <div *ngIf="confirmPassword.errors?.['required']">{{ getTranslation('Confirm the password.') }}</div>
      </small>
      <small class="block p-error" *ngIf="registerForm.errors?.['passwordMismatch'] && confirmPassword.valid && password.valid">
        {{ getTranslation('Passwords should match.') }}
      </small>
    </div>
    <p-button label="{{ getTranslation('Create Account') }}" styleClass="w-full orange-button" [disabled]="registerForm.invalid" type="submit"></p-button>
    <div class="mt-2">{{ getTranslation('Already have an account?') }} <a routerLink="/login" class="orange-link">{{ getTranslation('Sign in') }}</a></div>
  </p-card>
