import { Component } from '@angular/core';
import { CalculationService } from "../../services/calc.service";
import { Calculation } from "../../interfaces/calculation";
import { Router } from "@angular/router";
import { LanguageService } from '../../services/language.service';

@Component({
  selector: 'app-input',
  templateUrl: './input-component.component.html',
  styleUrls: ['./input-component.component.css']
})
export class InputComponent {

  inputValue: string = '';
  isInputFocused: boolean = false;
  calculationResult: string | undefined;
inputError: string = '';
  constructor(
    private calcService: CalculationService,
    private router: Router,
    private languageService: LanguageService
  ) { }
 ngOnInit() {
    this.languageService.languageChanged.subscribe(() => {
      this.languageChangedCallback();
    });
  }

  languageChangedCallback() {
    this.inputError = '';
  }
  onInputFocus() {
    this.isInputFocused = true;
  }

  onInputBlur() {
    setTimeout(() => {
      this.isInputFocused = false;
    }, 200);
  }

  addCharacter(character: string) {
    this.inputValue += character;
  }

  deleteLastCharacter() {
    this.inputValue = this.inputValue.slice(0, -1);
  }

  clearInput() {
    this.inputValue = '';
  }

  calculate() {
    const userId = localStorage.getItem('userId');

    const calcData: Calculation = {
      userId: userId,
      expression: this.inputValue,
      library: 'JAVA'
    };

    this.calcService.createCalculation(calcData as Calculation).subscribe(
      response => {
        this.calculationResult = response.data;
      }
    );
  }

getTranslation(key: string): string {
    return this.languageService.getTranslation(key);
  }
}
<div class="input-container">
  <input type="text" placeholder="{{ getTranslation('Enter what you want to calculate or know about') }}" [(ngModel)]="inputValue" (focus)="onInputFocus()" (blur)="onInputBlur()">
  <button class="btn orange-bg" (click)="calculate()">{{ getTranslation('=') }}</button>
</div>

<div class="math-input-container" [hidden]="!isInputFocused">
  <table>
    <tbody>
		  <tr>
        <td><button class="btn" (click)="addCharacter('1')">1</button></td>
        <td><button class="btn" (click)="addCharacter('2')">2</button></td>
        <td><button class="btn" (click)="addCharacter('3')">3</button></td>
      </tr>
      <tr>
        <td><button class="btn" (click)="addCharacter('4')">4</button></td>
        <td><button class="btn" (click)="addCharacter('5')">5</button></td>
        <td><button class="btn" (click)="addCharacter('6')">6</button></td>
      </tr>
      <tr>
        <td><button class="btn" (click)="addCharacter('7')">7</button></td>
        <td><button class="btn" (click)="addCharacter('8')">8</button></td>
        <td><button class="btn" (click)="addCharacter('9')">9</button></td>
      </tr>
      <tr>
        <td><button class="btn" (click)="addCharacter('0')">0</button></td>
        <td><button class="btn" (click)="addCharacter('.')">.</button></td>
        <td><button class="btn"(click)="clearInput()">{{ getTranslation('C') }}</button></td>
      </tr>
	  </tbody>
  </table>
  <button class="btn" (click)="addCharacter('(')">(</button>
  <button class="btn" (click)="addCharacter(')')">)</button>
  <button class="btn" (click)="addCharacter('^')">^</button>
  <button class="btn" (click)="addCharacter('*')">*</button>
  <button class="btn" (click)="addCharacter('/')">/</button>
  <button class="btn" (click)="addCharacter('+')">+</button>
  <button class="btn" (click)="addCharacter('-')">-</button>
  <button class="btn" (click)="addCharacter('π')">π</button>
  <button class="btn" (click)="addCharacter('e')">e</button>
  <button class="btn" (click)="addCharacter('sin(')">{{ getTranslation('sin') }}</button>
  <button class="btn" (click)="addCharacter('cos(')">{{ getTranslation('cos') }}</button>
  <button class="btn" (click)="addCharacter('tg(')">{{ getTranslation('tg') }}</button>
  <button class="btn" (click)="addCharacter('ctg(')">{{ getTranslation('ctg') }}</button>
  <button class="btn" (click)="addCharacter('sinh(')">{{ getTranslation('sinh') }}</button>
  <button class="btn" (click)="addCharacter('cosh(')">{{ getTranslation('cosh') }}</button>
  <button class="btn" (click)="addCharacter('tanh(')">{{ getTranslation('tanh') }}</button>
  <button class="btn" (click)="addCharacter('coth(')">{{ getTranslation('coth') }}</button>
  <!-- <button class="btn" (click)="addCharacter('√(')">{{ getTranslation('√') }}</button> -->
  <button class="btn" (click)="addCharacter('x')">x</button>
  <button class="btn" (click)="deleteLastCharacter()">{{ getTranslation('Backspace') }}</button>
  <button class="btn" (click)="clearInput()">{{ getTranslation('Clear') }}</button>
</div>

<div class="result-container" *ngIf="calculationResult !== ''">
  <p>{{ getTranslation('Result') }}:</p>
  <p>{{ calculationResult }}</p>
</div>
